import React from 'react';
import { connect } from 'react-redux';
import { fetchReadingBooks } from '../../actions/index';


class BooksReading extends React.Component {
    componentDidMount() {
        this.props.fetchReadingBooks();
    }

    renderList() {
        return this.props.readingBooks.map(book => {
            if(!book.user) {
                return
            } else {
            return (
                <div className="item" key={book.id}>
                <i className="large middle aligned icon book" />
                <div className="content">
                    {book.title}
                    <div className="description">
                    {book.author}
                    </div>
                </div>
                </div>
            );
            }
        });
    }

    render() {
        return (
            <div>
            <h2>Reading books</h2>
            <div className="ui celled list">{this.renderList()}</div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        readingBooks: Object.values(state.readingBooks),
        userId: this.state.auth
    };
}

export default connect(mapStateToProps, { fetchReadingBooks })(BooksReading);
