import React from 'react';
import { connect } from 'react-redux';
import { fetchBook, editBook } from '../../actions';
import BookForm from './BookForm';

class StreamEdit extends React.Component {
    componentDidMount() {
        this.props.fetchBook(this.props.match.params.id);
    }

    onSubmit = (formValues) => {
        this.props.editBook(this.props.match.params.id, formValues);
    }

    render() {
        if(!this.props.book) {
            return <div>Loading...</div>
        }
    return (
        <div>
            <h3>Edit a book</h3>
            <BookForm 
            initialValues={this.props.book}
            onSubmit={this.onSubmit}/>
        </div>
    );
    }
};

const mapStateToProps = (state, ownProps) => {
    return { book: state.books[ownProps.match.params.id]};
};

export default connect(mapStateToProps, { fetchBook, editBook })(StreamEdit);
