import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchBooks } from '../../actions/';

class BooksReading extends React.Component {
    componentDidMount() {
        this.props.fetchBooks();
    }

    renderList() {
        return this.props.books
        .filter(book => book.bookShelf === 'toread')
        .filter(book => book.userId === this.props.currentUserId)
        .map(book => {
                return (
                    <div className="item" key={book.id}>
                    <i className="large middle aligned icon book" />
                    <div className="content">
                        {book.title}
                        <div className="description">
                            {book.author}
                        </div>
                    </div>
                    <div className="right floated content">
                    <Link to={`/edit/${book.bookShelf}/${book.id}`} className="ui button primary">
                                Edit
                            </Link>
                            <button className="ui button primary">
                                Edit
                            </button>
                        </div>
                    </div>
                );
            });
        };

    render() {
        return (
            <div>
            <h2>Books to read</h2>
            <div className="ui celled list">{this.renderList()}</div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return { 
        books: Object.values(state.books),
        currentUserId: state.auth.userId    
    };
}

export default connect(mapStateToProps, { fetchBooks })(BooksReading);
