import books from '../api/books';
import { 
    SIGN_IN,
    SIGN_OUT,
    ADD_BOOK,
    FETCH_BOOKS_READ,
    FETCH_BOOKS_READING,
    FETCH_BOOKS_TO_READ,
    DELETE_BOOK,
    EDIT_BOOK
    } from './types';

export const signIn = (userId) => {
    return {
        type: SIGN_IN,
        payload: userId
    };
};

export const signOut = () => {
    return {
        type: SIGN_OUT,
    };
};

export const addBook = (formValues) => async (dispatch, getState) => {
    const { userId } = getState().auth
    const shelf = formValues.bookShelf;
    const response = await books.post(`/${shelf}`, { ...formValues, userId });

    dispatch({ type: ADD_BOOK, payload: response.data});
}


export const fetchReadBooks = () => async dispatch => {
    const response = await books.get(`/read`);

    dispatch({ type: FETCH_READ_BOOKS, payload: response.data})
}

export const fetchReadingBooks = () => async dispatch => {
    const response = await books.get('/reading')

    dispatch({ type: FETCH_BOOKS_READING, payload: response.data})
}

export const fetchToReadBooks = () => async dispatch => {
    const response = await books.get('/toread')

    dispatch({type: FETCH_BOOKS_TO_READ, payload: response.data})
}

export const editBook = (id, shelf, formValues) => async dispatch => {
    const response = await books.put(`/${shelf}/${id}`, formValues);

    dispatch({ type: EDIT_BOOK, payload: response.data})
}

export const deleteBook = (id, shelf) => async dispatch => {
    await books.delete(`${shelf}/${id}`);

    dispatch({ type: DELETE_BOOK, payload: id});
}
