import React from 'react';
import DeleteModal from '../DeleteModal';
import { connect } from 'react-redux';
import history from '../../history';
import { fetchBook } from '../../actions';

class DeleteBook extends React.Component {
    componentDidMount() {
        this.props.fetchBook(this.props.match.params.id);
    }

    renderActions() {
        return (
            <div>
                <button className="ui button negative">Delete</button>
                <button onClick={() => history.push('/')} className="ui button">Cancel</button>
            </div>
        );
    }

    render() {
        if(!this.props.book) {
            return <div>Loading</div>
        };

        return ( 
            <div>
                DeleteBook
                <DeleteModal
                    title="Delete Stream"
                    content="Are you sure you want to delete this stream"
                    actions={this.renderActions()}
                />
            </div>
        );
    }   
};

const mapStateToProps = (state, ownProps) => {
    return { book: state.books[ownProps.match.params.id] }
}

export default connect(mapStateToProps, { fetchBook })(DeleteBook);
