import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchBooks } from '../../actions';

class BooksReading extends React.Component {
    componentDidMount() {
        this.props.fetchBooks();
    }

    renderList() {
        return this.props.books
        .filter(book => book.bookShelf === 'reading')
        .filter(book => book.userId === this.props.currentUserId)
        .map(book => {
                return (
                    <div className="book" key={book.id}>
                    <div className="content">
                        <h2 className="heading-primary">{book.title}</h2>
                        <div className="description">
                            {book.author}<br></br>
                            {book.bookType}
                        </div>
                    </div>
                    <div className="right floated content">
                        <Link to={`/edit/${book.bookShelf}/${book.id}`} className="ui button primary">
                            Edit
                        </Link>
                        <Link to={`/deleteBook/${book.id}`} className="ui button primary">
                            Delete
                        </Link>
                        </div>
                    </div>
                );
            });
        };

    render() {
        return (
            <div>
            <h2>Reading books</h2>
            <div className="ui celled list">{this.renderList()}</div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        books: Object.values(state.books),
        currentUserId: state.auth.userId
    };
};

export default connect(mapStateToProps, { fetchBooks })(BooksReading);
