import React from 'react';
import { connect } from 'react-redux';
import { fetchReadBooks } from '../../actions';

class BooksRead extends React.Component {
    componentDidMount() {
        this.props.fetchReadBooks();
    }

    renderUser(book) {
        if (book.userId !== null) {
            return <div>{this.renderBook()}</div> 
        }
    }


    renderBook(userId) {
        if (userId == null) {
            return <div>Please log in to your account</div>
        } else {
        return this.props.readBooks.map(book => {
            return (
                <div className="item" key={book.id}>
                <i className="large middle aligned icon camera" />
                <div className="content">
                    {book.title}
                    <div className="description">
                        {book.author}
                    </div>
                </div>
                </div>
            );
        });
    }
}

    render() {
        return (
            <div>
            <h2>Read books</h2>
            <div className="ui celled list">{this.renderList()}</div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return { 
        readBooks: Object.values(state.readBooks),
        userId: state.auth.userId 
    };
};

export default connect(mapStateToProps, { fetchReadBooks })(BooksRead);
