import books from '../api/books';
import { SIGN_IN, SIGN_OUT } from './types';

export const signIn = (userId) => {
    return {
        type: SIGN_IN,
        payload: userId
    };
};

export const signOut = () => {
    return {
        type: SIGN_OUT,
    };
};

export const addBookRead = formValues => async dispatch => { 
    books.post('/read', formValues);
}

export const addBookReading = formValues => async dispatch => {
    books.post('/reading', formValues);
}
