import React from 'react';
import { connect } from 'react-redux';
import { fetchBooks } from '../../actions';

class BooksRead extends React.Component {
    componentDidMount() {
        this.props.fetchBooks();
    }

    renderList() {
        return this.props.books
        .filter(book => book.bookShelf === 'read')
        .map(book => {
                if(!book.userId) {
                    return 
                } else if (book.userId === this.props.currentUserId) {
                return (
                    <div className="item" key={book.id}>
                    <i className="large middle aligned icon book" />
                    <div className="content">
                        {book.title}
                        <div className="description">
                            {book.author}
                        </div>
                    </div>
                    <div className="right floated content">
                            <button className="ui button negative">
                                Delete
                            </button>
                            <button className="ui button primary">
                                Edit
                            </button>
                        </div>
                    </div>
                );

            }
        });
        };

    render() {
        return (
            <div>
            <h2>Read books</h2>
            <div className="ui celled list">{this.renderList()}</div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return { 
        books: Object.values(state.books),
        currentUserId: state.auth.userId 
    };
};

export default connect(mapStateToProps, { fetchBooks })(BooksRead);
