import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchReadingBooks } from '../../actions';

class BooksReading extends React.Component {
    componentDidMount() {
        this.props.fetchReadingBooks();
    }

    renderList() {
        return this.props.books.map(book => {
            if(book.bookShelf === 'reading') {
                if(!book.userId) {
                    return
                } else if (book.userId === this.props.currentUserId) {
                return (
                    <div className="item" key={book.bookShelf.id}>
                    <i className="large middle aligned icon book" />
                    <div className="content">
                        {book.title}
                        <div className="description">
                            {book.author}
                        </div>
                    </div>
                    <div className="right floated content">
                    <Link to={`/edit/${book.bookShelf}/${book.id}`} className="ui button primary">
                                Edit
                            </Link>
                            <button className="ui button primary">
                                Edit
                            </button>
                        </div>
                    </div>
                );
            } else {
                    return <div>Please log in to see data</div>
            }
        }
        });
    }

    render() {
        return (
            <div>
            <h2>Reading books</h2>
            <div className="ui celled list">{this.renderList()}</div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return {
        books: Object.values(state.books),
        currentUserId: state.auth.userId
    };
};

export default connect(mapStateToProps, { fetchReadingBooks })(BooksReading);
