import books from '../api/books';
import { SIGN_IN, SIGN_OUT, ADD_BOOK_READ } from './types';

export const signIn = (userId) => {
    return {
        type: SIGN_IN,
        payload: userId
    };
};

export const signOut = () => {
    return {
        type: SIGN_OUT,
    };
};

export const addBookRead = formValues => async dispatch => { 
    const response = await books.post('/read', formValues);

    dispatch({ type: ADD_BOOK_READ, payload: response.data})
}

export const addBookReading = formValues => async dispatch => {
    const response = await books.post('/reading', formValues);
}

export const addBookToRead = formValues => async dispatch => {
    const response = await books.post('/toread', formValues);

}
