import React from 'react';
import { connect } from 'react-redux';
import { fetchReadBooks } from '../../actions';

class BooksRead extends React.Component {
    componentDidMount() {
        this.props.fetchReadBooks();
    }

    renderList() {
        if(this.props.bookShelf === '"currentlyReading')
        return this.props.books.map(book => {
            return (
                <div className="item" key={book.id}>
                <i className="large middle aligned icon camera" />
                <div className="content">
                    {book.title}
                    <div className="description">
                    {book.author}
                    </div>
                </div>
                </div>
            );
        });
    }

    render() {
        return (
            <div>
            <h2>Read books</h2>
            <div className="ui celled list">{this.renderList()}</div>
            </div>
        );
    }
}

const mapStateToProps = state => {
    return { books: Object.values(state.books) };
};

export default connect(mapStateToProps, { fetchReadBooks })(BooksRead);
